clear;a = 3;b = 1;t = linspace(0, 2*pi);e_x = a * cos(t);e_y = b * sin(t);inv_term = (a.^2 .* sin(t).^2 + b.^2 .* cos(t).^2) / (a * b);eta_x = a * cos(t) + inv_term .* (-b * cos(t));eta_y = b * sin(t) + inv_term .* (-a * sin(t));# Ref: https://mathworld.wolfram.com/EllipseEvolute.html# Use this to check if i'm right#ref_x = (a^2-b^2) / a * cos(t) .^3;#ref_y = (b^2-a^2) / b * sin(t) .^3;figure;hold on;axis([-10, 10, -10, 10]);axis equalplot(e_x,e_y);plot(eta_x,eta_y);#plot(ref_x,ref_y);# Draw circlesfor ts = [pi pi/2 11*pi/6]  inv_term_c = (a^2 * sin(ts)^2 + b^2 * cos(ts)^2) / (a * b);  xc = a * cos(ts) + inv_term_c * (-b * cos(ts));  yc = b * sin(ts) + inv_term_c * (-a * sin(ts));  curvature = (a*b) / (sqrt(a^2 * sin(ts)^2 + b^2 * cos(ts)^2))^3;    tr = linspace(0, 2*pi);    xr = xc + (1 / curvature) * cos(tr);  yr = yc + (1 / curvature) * sin(tr);  plot(xr, yr);end